import React, { useState } from "react";
import Project2 from "./components/Project2/Project2";
import HomePage from "./Pages/HomePage";
import Project1 from "./components/Project1/Project1";


function App() {
  const [currentPage, setCurrentPage] = useState("home");

  function pageChangeHandler(newPage) {
    setCurrentPage(newPage);
  };

  return (
    <React.Fragment>
      {currentPage==="home" && <HomePage pageChanger={pageChangeHandler}></HomePage>}
      {currentPage==="p1" && <Project1 pageChanger={pageChangeHandler}></Project1>}
      {currentPage==="p2" && <Project2 pageChanger={pageChangeHandler}></Project2>}
    </React.Fragment>


  );
}

export default App;
