import React, { useState } from "react";
import AboutMe from "../components/About/AboutMe";
import ProjectList from "../components/Project/ProjectList";

import project1Image from "../assets/Python Developer.jpg";
import Modal from "../components/UI/Modal/Modal";

const projects = [
  {
    id: "Project1",
    name: "Linkedin Job Scrapper",
    image: project1Image,
    description: "This is a small web scraping project to scrape linkedin Jobs. The scrapper has a GUI that will take a search term(eg. 'Python Developer') and a Location(eg. 'Turkey') and then it will return the mostly mentioned tech skills required for that position.",
    tags: ["Python, Selenium"]
  },
  {
    id: "Project2",
    name: "Project2",
    image: "https://picsum.photos/id/237/800/600",
    description: "Project2 description",
    tags: []
  },
  {
    id: "Project3",
    name: "Project3",
    image: "https://picsum.photos/id/17/800/600",
    description: "Project3 description",
    tags: []
  },
  {
    id: "Project4",
    name: "Project4",
    image: "https://picsum.photos/id/133/800/600",
    description: "Project4 description",
    tags: []
  },
  {
    id: "Project5",
    name: "Project5",
    image: "https://picsum.photos/id/44/800/600",
    description: "Project5 description",
    tags: []
  }
];


function HomePage() {
  const [currentProject, setCurrentProject] = useState(projects[0]);
  const [modalVisible, setModalVisible] = useState(true);

  const modalVisibleHandler = () => {
    setModalVisible((prevState) => {
      prevState ? setModalVisible(false) : setModalVisible(true);
    });
  };

  return (
    <React.Fragment>
      <Modal onCloseModal={modalVisibleHandler} visible={modalVisible} project={currentProject}></Modal>
      <AboutMe></AboutMe>
      <ProjectList projects={projects}></ProjectList>
    </React.Fragment>
    );
};

export default HomePage;
