import React, { useState, useEffect } from 'react';

import styles from './Project2.module.css';

function Project2(props) {
    const [shownImage, setShownImage] = useState(null);

    function imageUploadHandler(event) {
        if(event.target.files && event.target.files[0]) {
            const fileName = event.target.files[0]['name'].toLowerCase();
            if(fileName.includes('.jpg') || fileName.includes('.png') || fileName.includes('.jpeg')) {
                const img = document.querySelector('#myImg');

                img.onload = () => {
                    URL.revokeObjectURL(img.src)
                };

                //img.src = URL.createObjectURL(event.target.files[0]);
                setShownImage(URL.createObjectURL(event.target.files[0]));
            }
        }
    };

    return (
    <React.Fragment>
    <h1>
        <img alt="Back" className={styles["back-button"]} width="25rem" src="https://image.flaticon.com/icons/png/512/60/60577.png"/>
        Web Content Filtering
    </h1>
    <div className={styles['image-div']}><img style={{width: "800px", height: "600px"}} alt="Classification" id="myImg" src={shownImage}/></div>
    <input onChange={imageUploadHandler} type='file' />
    </React.Fragment>
    );
};

export default Project2;
