import React from 'react';

import styles from './Modal.module.css';

function Overlay(props) {
    return (
        <div className={styles.overlay}  onClick={props.onClick}/>
    )
};

function Modal(props) {
    const topPercent = 100*(0.5 + window.pageYOffset / (document.body.scrollHeight*0.5));

    function closeModal() {
        props.onCloseModal();
    };

    function pageChangeHandler() {
        props.pageChanger(props.project.page);
    };

    return (
        <React.Fragment>
            {props.visible && <Overlay onClick={closeModal}></Overlay>}
            <div style={{top: `${topPercent}%`}} className={`${styles.modal} ${!props.visible && styles.hidden}`}>
                <button onClick={closeModal} className={styles['close-modal']}>&times;</button>
                <h1>{props.project?.name}</h1>
                <p>{props.project?.description}</p>
                <a onClick={pageChangeHandler} href="blank">Go to project</a>
            </div>
        </React.Fragment>
    );
};

export default Modal;
